    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
         // Estas líneas leen los datos, pero se ignoran para el propósito del mensaje específico
        int selectedYear = DtYearEvent.getYear();
        int selectedMonth = dtMonthEvent.getMonth() + 1; 

        // Simula que la búsqueda en la base de datos no encontró resultados.
        System.out.println("Buscando eventos para Año: " + selectedYear + ", Mes: " + selectedMonth + "...");

        // Muestra el mensaje de error solicitado
        JOptionPane.showMessageDialog(this, 
        "No hay eventos registrados para el mes seleccionado.",
        "Sin Resultados", 
        JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_btnBuscarActionPerformed

    public static void main(String args[]) {
        javax.swing.SwingUtilities.invokeLater(() -> {
            // 1. Crea el contenedor principal (JFrame)
            JFrame frame = new JFrame("Búsqueda de Eventos por Mes");
            frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
            
            // 2. Añade tu JPanel (FrmMonth) al Frame
            frame.add(new FrmMonth());
            
            // 3. Ajusta y muestra
            frame.pack();
            frame.setLocationRelativeTo(null);
            frame.setVisible(true);
        });
    }

